mapper.sql:
		FROM 
			code a
		LEFT JOIN
			codeGroup b
		ON
			a.codeGroup_cgSeq = b.cgSeq
		WHERE 1=1
       		AND a.cDelNy = #{shDelNy} 				


					AND a.cRegDate BETWEEN #{shDateStartDB} AND #{shDateEndDB}


					AND a.cMofDate BETWEEN #{shDateStartDB} AND #{shDateEndDB}



	        	AND a.codeGroup_cgSeq = #{shValue}
	            AND b.cgName LIKE CONCAT('%',#{shValue},'%')
	            AND a.cSeq = #{shValue}
	            AND a.cName LIKE CONCAT('%',#{shValue},'%')

		ORDER BY a.cSeq DESC
