mapper.sql:
FROM orders a left join ordersProduct b on a.oSeq = b.orders_oSeq left join product c on b.product_pSeq = c.pSeq left join vender d on c.vender_vSeq = d.vSeq WHERE 1=1
AND a.oDate BETWEEN #{shDateStartDB} AND #{shDateEndDB}
AND a.oSeq LIKE CONCAT('%',#{shValue},'%') AND a.oReceiver LIKE CONCAT('%',#{shValue},'%')

mapper.select:
SELECT a.oSeq, a.oStateCd, a.oDate, a.oReceiver, a.oReceiverTel, a.oReceiverAddr, a.user_uSeq FROM orders a WHERE 1=1
AND a.oDate BETWEEN #{shDateStartDB} AND #{shDateEndDB}
AND a.oStateCd = #{shStateOption}
AND a.oSeq LIKE CONCAT('%',#{shValue},'%') AND a.oReceiver LIKE CONCAT('%',#{shValue},'%')
ORDER BY a.oSeq DESC LIMIT #{rowNumToShow} OFFSET #{startRnumForMysql}

mapper.update:
UPDATE orders
SET
	oStateCd=#{oStateCd}
WHERE
	oSeq=#{oSeq}

	UPDATE ordersProduct
	SET
		opStateCd=#{opStateCd}

	WHERE
        opSeq=#{opSeq}
