mapper.sql:
FROM orders a left join ordersProduct b on a.oSeq = b.orders_oSeq left join product c on b.product_pSeq = c.pSeq left join vender d on c.vender_vSeq = d.vSeq WHERE 1=1
AND a.oDate BETWEEN #{shDateStartDB} AND #{shDateEndDB}
AND a.oSeq LIKE CONCAT('%',#{shValue},'%') AND a.oReceiver LIKE CONCAT('%',#{shValue},'%')

mapper.select:
SELECT


ORDER BY a.oSeq DESC
LIMIT #{rowNumToShow} OFFSET #{startRnumForMysql}

mapper.update:
UPDATE orders
SET
	oStateCd=#{oStateCd}
WHERE
	oSeq=#{oSeq}
